import React, { useEffect } from 'react'
import './cards.css'
import imgDoight from '../../assets/img/doight.svg'
import imgFleche from '../../assets/img/triangleHautBas.svg'
import carrer from '../../assets/img/groupe.svg'
import { useRef } from 'react'
import { useState } from 'react'
import { useInView } from "react-intersection-observer";

export default function Cards({color,setcolor}) {

  const englobeCardTitre =useRef({});
  const titreCard = useRef();
  const cardUne = useRef();
  const cardTrois = useRef();
  const cardDeux = useRef();
  const { ref:breackpointUn,inView:visible } = useInView({triggerOnce:true});
  const { ref:breackpointDeux,inView:visible2} = useInView({triggerOnce:true});
  const { ref:breackpointTrois,inView:visible3 } = useInView({triggerOnce:true});
  const { ref:breackpointQuatre,inView:visible4 } = useInView({triggerOnce:true});
  const { ref:breackpointCinq,inView:visible5 } = useInView({triggerOnce:true});
  const { ref:breackpointSix,inView:visible6 } = useInView({triggerOnce:true});
  const { ref:breackpointSept,inView:visible7 } = useInView({triggerOnce:true});
  const { ref:breackpointHuigt,inView:visible8 } = useInView({triggerOnce:true});
  const { ref:breackpointNeuf,inView:visible9 } = useInView({triggerOnce:true});
  const [scrollY, setscrollY] = useState(0);
  
  const [state, setstate] = useState([<div ref={breackpointUn} className='breackpointUn'></div>,<div ref={breackpointDeux} className='breackpointDeux'></div>,
    <div ref={breackpointTrois} className='breackpointTrois'></div>,<div ref={breackpointQuatre} className='breackpointQuatre'></div>,
    <div ref={breackpointCinq} className='breackpointCinq'></div>,<div ref={breackpointSix} className='breackpointSix'></div>
    ,<div ref={breackpointSept} className='breackpointSept'></div>,<div ref={breackpointHuigt} className='breackpointHuigt'></div>
    ,<div ref={breackpointNeuf} className='breackpointNeuf'></div>]);



  useEffect(() => {
    if(visible){
      setcolor("#BFBFBF");
      englobeCardTitre.current.style.backgroundColor = "#BFBFBF";
      titreCard.current.style.color = "#4F4F4F";
    }
    if(visible2 ){
      setcolor("#5F5F5F");
      englobeCardTitre.current.style.backgroundColor = "#5F5F5F";
      titreCard.current.style.color = "#B2B2B2";
    }
    if(visible3 ){
      setcolor("#181818");
      englobeCardTitre.current.style.backgroundColor = "#181818";
      titreCard.current.style.color = "#FBFBFB";
    }
    if(visible4){
      titreCard.current.style.opacity = "0.7";
      cardUne.current.style.transform = `translate3d(88px,44px,0px) rotateZ(-8deg)`;
      cardTrois.current.style.transform = `translate3d(-88px,44px,0px) rotateZ(8deg)`;
    }
    if(visible5){
      titreCard.current.style.opacity = "0.4";
      cardUne.current.style.transform = `translate3d(66px,33px,0px) rotateZ(-6deg)`;
        cardTrois.current.style.transform = `translate3d(-66px,33px,0px) rotateZ(6deg)`;
    }
    if(visible6 ){
      titreCard.current.style.opacity = "0";
      cardUne.current.style.transform = `translate3d(44px,22px,0px) rotateZ(-4deg)`;
      cardTrois.current.style.transform = `translate3d(-44px,22px,0px) rotateZ(4deg)`;
    }
    if(visible7 ){
      cardUne.current.style.transform = `translate3d(22px,11px,0px) rotateZ(-2deg)`;
     cardTrois.current.style.transform = `translate3d(-22px,11px,0px) rotateZ(2deg)`;
    }
    if(visible8 ){
      
      cardUne.current.style.transform = `translate3d(0px,0px,0px) rotateZ(0deg)`;
      cardTrois.current.style.transform = `translate3d(0px,0px,0px) rotateZ(0deg)`;
    }
  
    return () => {
    };
  }, [visible,visible2,visible3,visible4,visible5,visible6,visible7,visible8,visible9,setcolor,scrollY,breackpointUn])

 
  return (
    <div ref={englobeCardTitre} className='englobeCardTitre'>
                
      {state.map((el)=>{
        return(
          el
        )
      })}
      <div ref={titreCard} className='titre'>Collections are your most <br></br>important pages, but are <br></br>frustrating to manage</div>
      <div  className="allcards">
         <div ref={cardUne} id='card1' className="uniteCard">
            <img className='imgCard' src={imgDoight} alt="" />
            <p className='p1Card'>Non-Visual<br></br>Curation</p>
            <p style={{opacity:"0.3"}} className='p2Card'>Manually curating your collections<br></br>feels like doing your taxes.</p>
         </div>
         <div ref={cardDeux} id='card2'  className="uniteCard">
            <img  className='imgCard' src={imgFleche} alt="" />
            <p  className='p1Card'>Limited<br></br>Sorting</p>
            <p style={{opacity:"0.6"}} className='p2Card'>It's too basic to sort your collections <br></br>by "New in" or "Best Sellers".</p>
         </div>
         <div ref={cardTrois} id='card3'  className="uniteCard">
            <img className='imgCard' src={carrer} alt="" />
            <p className='p1Card'>Messy<br></br>Data</p>
            <p style={{opacity:"0.7"}} className='p2Card'>You need 5 tabs open to take data-<br></br>driven decisions from excell sheets.</p>
         </div>
      </div>
    </div>
  )
}
