import React, { useEffect } from 'react'
import './cards.css'
import imgDoight from '../../assets/img/doight.svg'
import imgFleche from '../../assets/img/triangleHautBas.svg'
import carrer from '../../assets/img/groupe.svg'
import { useRef } from 'react'
import { useState } from 'react'
import { useInView } from "react-intersection-observer";

export default function Cards({color,setcolor}) {

  const englobeCardTitre =useRef({});
  const titreCard = useRef();
  const cardUne = useRef();
  const cardTrois = useRef();
  const cardDeux = useRef();
  const [hauteurBkUn, sethauteurBkUn] = useState(0);
  const breackpointUn=useRef();
  const [hauteurBkDeux, sethauteurBkDeux] = useState(0);
  const breackpointDeux=useRef();
  const [hauteurBkTrois, sethauteurBkTrois] = useState(0);
  const breackpointTrois=useRef();
  const [hauteurBkQuatre, sethauteurBkQuatre] = useState(0);
  const breackpointQuatre=useRef();
  const [hauteurBkCinq, sethauteurBkCinq] = useState(0);
  const breackpointCinq=useRef();
  const [hauteurBkSix, sethauteurBkSix] = useState(0);
  const breackpointSix=useRef();
  const [hauteurBkSept, sethauteurBkSept] = useState(0);
  const breackpointSept=useRef();
  const [hauteurBkHuight, sethauteurBkHuight] = useState(0);
  const breackpointHuigt=useRef();
  const [hauteurBkNeuf, sethauteurBkNeuf] = useState(0);
  const breackpointNeuf=useRef();
 
  
  const [state, setstate] = useState([<div ref={breackpointUn} className='breackpointUn'></div>,<div ref={breackpointDeux} className='breackpointDeux'></div>,
    <div ref={breackpointTrois} className='breackpointTrois'></div>,<div ref={breackpointQuatre} className='breackpointQuatre'></div>,
    <div ref={breackpointCinq} className='breackpointCinq'></div>,<div ref={breackpointSix} className='breackpointSix'></div>
    ,<div ref={breackpointSept} className='breackpointSept'></div>,<div ref={breackpointHuigt} className='breackpointHuigt'></div>
    ,<div ref={breackpointNeuf} className='breackpointNeuf'></div>]);



  useEffect(() => {
    function setCouleur(setteur,breackpoint,couleurUne,couleurDeux,tailleEcran){
      setteur(breackpoint.current.getBoundingClientRect().top);
      if(hauteurBkUn<tailleEcran){
        setcolor(couleurUne);
        englobeCardTitre.current.style.backgroundColor =couleurUne;
        titreCard.current.style.color = cardDeux;
      }
      if(hauteurBkUn>tailleEcran){
        setcolor("#F3F3F3");
        englobeCardTitre.current.style.backgroundColor = "#F3F3F3";
        titreCard.current.style.color = "#181818";
      }
    }

    function onScroll(){
      const tailleEcran = window.innerHeight;
      setCouleur(sethauteurBkUn,breackpointUn,"#BFBFBF","#4F4F4F",tailleEcran);
      setCouleur(sethauteurBkUn,breackpointUn,"#BFBFBF","#4F4F4F",tailleEcran);
    }

    window.addEventListener('scroll',onScroll);
  
    return () => {
      window.removeEventListener('scroll',onScroll);
    };
  }, [hauteurBkUn,hauteurBkDeux,hauteurBkTrois,hauteurBkQuatre,hauteurBkCinq,hauteurBkSix,hauteurBkSept,hauteurBkHuight,hauteurBkNeuf,setcolor])

 
  return (
    <div ref={englobeCardTitre} className='englobeCardTitre'>
                
      {state.map((el)=>{
        return(
          el
        )
      })}
      <div ref={titreCard} className='titre'>Collections are your most <br></br>important pages, but are <br></br>frustrating to manage</div>
      <div  className="allcards">
         <div ref={cardUne} id='card1' className="uniteCard">
            <img className='imgCard' src={imgDoight} alt="" />
            <p className='p1Card'>Non-Visual<br></br>Curation</p>
            <p style={{opacity:"0.3"}} className='p2Card'>Manually curating your collections<br></br>feels like doing your taxes.</p>
         </div>
         <div ref={cardDeux} id='card2'  className="uniteCard">
            <img  className='imgCard' src={imgFleche} alt="" />
            <p  className='p1Card'>Limited<br></br>Sorting</p>
            <p style={{opacity:"0.6"}} className='p2Card'>It's too basic to sort your collections <br></br>by "New in" or "Best Sellers".</p>
         </div>
         <div ref={cardTrois} id='card3'  className="uniteCard">
            <img className='imgCard' src={carrer} alt="" />
            <p className='p1Card'>Messy<br></br>Data</p>
            <p style={{opacity:"0.7"}} className='p2Card'>You need 5 tabs open to take data-<br></br>driven decisions from excell sheets.</p>
         </div>
      </div>
    </div>
  )
}
