import React, { useEffect } from 'react'
import './cards.css'
import imgDoight from '../../assets/img/doight.svg'
import imgFleche from '../../assets/img/triangleHautBas.svg'
import carrer from '../../assets/img/groupe.svg'
import { useRef } from 'react'
import { useState } from 'react'
import { useInView } from "react-intersection-observer";

export default function Cards({color,setcolor}) {

  const englobeCardTitre =useRef({});
  const titreCard = useRef();
  const cardUne = useRef();
  const cardTrois = useRef();
  const cardDeux = useRef();
  const [booleanBkUn,setbooleanBkUn] = useState(true);const breackpointUn=useRef(); const [hauteurBkUn, sethauteurBkUn] = useState();
  const [hauteurBkDeux, sethauteurBkDeux] = useState();const [booleanBkDeux,setbooleanBkDeux] = useState(true);const breackpointDeux=useRef();
  const [hauteurBkTrois, sethauteurBkTrois] = useState();const breackpointTrois=useRef();const [booleanBkTrois,setbooleanBkTrois] = useState(true)
  const [hauteurBkQuatre, sethauteurBkQuatre] = useState();const breackpointQuatre=useRef();const [booleanBkQuatre,setbooleanBkQuatre] = useState(true)
  const [hauteurBkCinq, sethauteurBkCinq] = useState();const breackpointCinq=useRef();const [booleanBkCinq,setbooleanBkCinq] = useState(true)
  const [hauteurBkSix, sethauteurBkSix] = useState();const breackpointSix=useRef();const [booleanBkSix,setbooleanBkSix] = useState(true)
  const [hauteurBkSept, sethauteurBkSept] = useState(); const breackpointSept=useRef();const [booleanBkSept,setbooleanBkSept] = useState(true)
  const [hauteurBkHuight, sethauteurBkHuight] = useState();const breackpointHuigt=useRef();const [booleanBkHuight,setbooleanBkHuight] = useState(true)
  const [hauteurBkNeuf, sethauteurBkNeuf] = useState();const breackpointNeuf=useRef();const [booleanBkNeuf,setbooleanBkNeuf] = useState(true)
 
  
  const [state, setstate] = useState([<div ref={breackpointUn} className='breackpointUn'></div>,<div ref={breackpointDeux} className='breackpointDeux'></div>,
    <div ref={breackpointTrois} className='breackpointTrois'></div>,<div ref={breackpointQuatre} className='breackpointQuatre'></div>,
    <div ref={breackpointCinq} className='breackpointCinq'></div>,<div ref={breackpointSix} className='breackpointSix'></div>
    ,<div ref={breackpointSept} className='breackpointSept'></div>,<div ref={breackpointHuigt} className='breackpointHuigt'></div>
    ,<div ref={breackpointNeuf} className='breackpointNeuf'></div>]);



  useEffect(() => {
    function setCouleur(setteur,hauteurbK,breackpoint,booleanBk,setBooleanBk,couleurUne,couleurDeux,couleurTrois,couleursQuatre,tailleEcran){
      setteur(breackpoint.current.getBoundingClientRect().top);
      if(hauteurbK<tailleEcran && booleanBk ){
        setcolor(couleurUne);
        englobeCardTitre.current.style.backgroundColor =couleurUne;
        titreCard.current.style.color = couleurDeux;
        setBooleanBk(false);
      }
      if(hauteurbK>tailleEcran && !booleanBk){
        setcolor(couleurTrois);
        englobeCardTitre.current.style.backgroundColor = couleurTrois;
        titreCard.current.style.color = couleursQuatre;
        setBooleanBk(true);
      }
    }

    function setTransform (setteur,hauteurbK,breackpoint,booleanBk,setBooleanBk,opacityUne,translateUne,translateDeux,opacityDeux,translateTrois,translateQuatre,tailleEcran){
      setteur(breackpoint.current.getBoundingClientRect().top);
      if(hauteurbK<tailleEcran && booleanBk ){
        console.log("oui");
        titreCard.current.style.opacity = opacityUne;
        cardUne.current.style.transform = translateUne;
        cardTrois.current.style.transform = translateDeux;
        setBooleanBk(false);
      }
      if(hauteurbK>tailleEcran && !booleanBk){
        titreCard.current.style.opacity = opacityDeux;
        cardUne.current.style.transform = translateTrois;
        cardTrois.current.style.transform = translateQuatre;
        setBooleanBk(true);
      }
    }

    function onScroll(){
      const tailleEcran = window.innerHeight;
      setCouleur(sethauteurBkUn,hauteurBkUn,breackpointUn,booleanBkUn,setbooleanBkUn,"#BFBFBF","#4F4F4F","#F3F3F3","#181818",tailleEcran);
      setCouleur(sethauteurBkDeux,hauteurBkDeux,breackpointDeux,booleanBkDeux,setbooleanBkDeux,"#5F5F5F","#B2B2B2","#BFBFBF","#4F4F4F",tailleEcran);
      setCouleur(sethauteurBkTrois,hauteurBkTrois,breackpointTrois,booleanBkTrois,setbooleanBkTrois,"#181818","#FBFBFB","#5F5F5F","#B2B2B2",tailleEcran);
      setTransform(setbooleanBkQuatre,hauteurBkQuatre,breackpointQuatre,booleanBkQuatre,setbooleanBkQuatre,"0.7",`translate3d(88px,44px,0px) rotateZ(-8deg)`,`translate3d(-88px,44px,0px) rotateZ(8deg)`,"1",`translate3d(-110px,55px,0px) rotateZ(-10deg)`,`translate3d(110px,55px,0px) rotateZ(10deg)`,tailleEcran);
    }

    window.addEventListener('scroll',onScroll);
  
    return () => {
      window.removeEventListener('scroll',onScroll);
    };
  }, [hauteurBkUn,hauteurBkDeux,hauteurBkTrois,hauteurBkQuatre,hauteurBkCinq,hauteurBkSix,hauteurBkSept,hauteurBkHuight,hauteurBkNeuf,setcolor,booleanBkUn,booleanBkDeux,booleanBkTrois,booleanBkQuatre])

 
  return (
    <div ref={englobeCardTitre} className='englobeCardTitre'>
                
      {state.map((el)=>{
        return(
          el
        )
      })}
      <div ref={titreCard} className='titre'>Collections are your most <br></br>important pages, but are <br></br>frustrating to manage</div>
      <div  className="allcards">
         <div ref={cardUne} id='card1' className="uniteCard">
            <img className='imgCard' src={imgDoight} alt="" />
            <p className='p1Card'>Non-Visual<br></br>Curation</p>
            <p style={{opacity:"0.3"}} className='p2Card'>Manually curating your collections<br></br>feels like doing your taxes.</p>
         </div>
         <div ref={cardDeux} id='card2'  className="uniteCard">
            <img  className='imgCard' src={imgFleche} alt="" />
            <p  className='p1Card'>Limited<br></br>Sorting</p>
            <p style={{opacity:"0.6"}} className='p2Card'>It's too basic to sort your collections <br></br>by "New in" or "Best Sellers".</p>
         </div>
         <div ref={cardTrois} id='card3'  className="uniteCard">
            <img className='imgCard' src={carrer} alt="" />
            <p className='p1Card'>Messy<br></br>Data</p>
            <p style={{opacity:"0.7"}} className='p2Card'>You need 5 tabs open to take data-<br></br>driven decisions from excell sheets.</p>
         </div>
      </div>
    </div>
  )
}
